let raw = $input.first().json.output || "";

// Bersihkan block ```json / ```php / ```
raw = raw.replace(/```[a-zA-Z]*/g, "").replace(/```/g, "").trim();

function safeParse(str) {
  try {
    return JSON.parse(str);
  } catch {
    return null;
  }
}

// Rekursif parse string JSON sampai jadi object/array
function recursiveJSON(str) {
  let parsed = safeParse(str);
  while (typeof parsed === "string") {
    let temp = safeParse(parsed);
    if (temp === null) break;
    parsed = temp;
  }
  return parsed;
}

// Deep parse (untuk dalamnya juga)
function deepParse(obj) {
  if (typeof obj === "string") {
    return recursiveJSON(obj) || obj;
  } else if (Array.isArray(obj)) {
    return obj.map(x => deepParse(x));
  } else if (typeof obj === "object" && obj !== null) {
    let newObj = {};
    for (let k in obj) {
      newObj[k] = deepParse(obj[k]);
    }
    return newObj;
  }
  return obj;
}

let parsed = recursiveJSON(raw);
parsed = deepParse(parsed);

if (parsed) {
  return Array.isArray(parsed)
    ? parsed.map(x => ({ json: x }))
    : [{ json: parsed }];
}

// fallback
return [{ json: { text: raw, error: "Invalid JSON" } }];

